import { Link, NavLink } from "react-router-dom";
import { useState } from "react";
import { useEffect } from "react";
import LoginRegister from "./LoginRegister";
import { useLocation } from "react-router";
import { useHistory } from "react-router-dom";


function Navbar(props) {

  const [category, setCategory] = useState([]);
  const nav = document.querySelector(".nav-ul");
  const { pathname } = useLocation();
  const [user, setUser] = useState([]);
  const [profile, setProfile] = useState([]);

  const history = useHistory();



  useEffect(() => {
    const hi2 = async () => {
      await fetch("http://localhost:8000/api/user/", {
        method: "GET",
        headers: {
          Authorization: "Bearer " + localStorage.getItem("token"),
        },
      })
        .then((data) => data.json())
        .then((user) => (
          setUser(user),
          fetch("http://localhost:8000/api/profile_list/")
            .then((res) => res.json())
            .then((data) =>
              setProfile(data.filter((i) => i.user === user["id"]))
            )
        )
        );
    };
    hi2();
  }, [pathname]);

  useEffect(() => {
    const hi = async () => {
      await fetch("http://localhost:8000/api/category_list/")
        .then((res2) => res2.json())
        .then((data2) => setCategory(data2));
    };

    hi();
  }, [setCategory]);

  function navclick() {
    nav.classList.remove("open");
    const menu = document.querySelector(".menu");
    menu.classList.remove("openmenu");
  }

  function openMenu() {
    nav.classList.toggle("open");
    const menu = document.querySelector(".menu");
    menu.classList.toggle("openmenu");
  }

  function signin() {
    const loginregisterbg = document.querySelector(".login-registerbg");
    loginregisterbg.classList.toggle("d-active");
    const login = document.querySelector(".login-register");
    login.classList.toggle("d-active");
    setTimeout(() => {
      document.querySelector(".login-register-form").classList.toggle("scaled");
    }, 10);
  }

  function categories() {
    const dropdownmobile = document.querySelector(".mobile-dropdown-category");
    const categorybg = document.querySelector(".categorybg");
    dropdownmobile.classList.toggle("mobile-dropdown-open");
    categorybg.classList.toggle("d-active");
  }

  function onclickcategories() {
    const dropdownmobile = document.querySelector(".mobile-dropdown-category");
    const categorybg = document.querySelector(".categorybg");
    dropdownmobile.classList.toggle("mobile-dropdown-open");
    categorybg.classList.toggle("d-active");
    navclick();
  }

  function Logout() {
    localStorage.removeItem("token");
    setUser("");
    document
      .querySelector(".bottom-alert")
      .classList.toggle("open-bottom-alert");
    document.querySelector(".bottom-alert").classList.remove("success");
    document.querySelector(".bottom-alert").classList.add("warning");

    document.querySelector("#bottom-alert-text").innerHTML =
      "Siz uğurla çıxış etdiniz";
    setTimeout(() => {
      document
        .querySelector(".bottom-alert")
        .classList.toggle("open-bottom-alert");
    }, 2000);
    history.push("/");
  }

  function accountdropdown() {
    const accountdropdownz = document.querySelector(".account-dropdown");
    accountdropdownz.classList.toggle("d-active");
    setTimeout(() => {
      accountdropdownz.classList.toggle("account-dropdown-animate");
    }, 20);
  }
  function accountdropdown2() {
    navclick();
    const accountdropdownz = document.querySelector(".account-dropdown");
    accountdropdownz.classList.toggle("d-active");
    setTimeout(() => {
      accountdropdownz.classList.toggle("account-dropdown-animate");
    }, 20);
  }





  return (
    <>
      <header className="flex flex-wrap sm:justify-start sm:flex-nowrap z-50 w-full bg-white text-sm py-4 dark:bg-gray-800">
  <nav className="max-w-[85rem] w-full mx-auto px-4 sm:flex sm:items-center sm:justify-between" aria-label="Global">
    <div className="flex items-center justify-between">
      <a className="flex-none text-xl font-semibold dark:text-white" href="#">Brand</a>
      <div className="sm:hidden">
        <button type="button" className="hs-collapse-toggle p-2 inline-flex justify-center items-center gap-2 rounded-md border font-medium bg-white text-gray-700 shadow-sm align-middle hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-offset-white focus:ring-blue-600 transition-all text-sm dark:bg-slate-900 dark:hover:bg-slate-800 dark:border-gray-700 dark:text-gray-400 dark:hover:text-white dark:focus:ring-offset-gray-800" data-hs-collapse="#navbar-alignment" aria-controls="navbar-alignment" aria-label="Toggle navigation">
          <svg className="hs-collapse-open:hidden w-4 h-4" width="16" height="16" fill="currentColor" viewBox="0 0 16 16">
            <path fill-rule="evenodd" d="M2.5 12a.5.5 0 0 1 .5-.5h10a.5.5 0 0 1 0 1H3a.5.5 0 0 1-.5-.5zm0-4a.5.5 0 0 1 .5-.5h10a.5.5 0 0 1 0 1H3a.5.5 0 0 1-.5-.5zm0-4a.5.5 0 0 1 .5-.5h10a.5.5 0 0 1 0 1H3a.5.5 0 0 1-.5-.5z"/>
          </svg>
          <svg className="hs-collapse-open:block hidden w-4 h-4" width="16" height="16" fill="currentColor" viewBox="0 0 16 16">
            <path d="M4.646 4.646a.5.5 0 0 1 .708 0L8 7.293l2.646-2.647a.5.5 0 0 1 .708.708L8.707 8l2.647 2.646a.5.5 0 0 1-.708.708L8 8.707l-2.646 2.647a.5.5 0 0 1-.708-.708L7.293 8 4.646 5.354a.5.5 0 0 1 0-.708z"/>
          </svg>
        </button>
      </div>
    </div>
    <div id="navbar-alignment" className="hs-collapse hidden overflow-hidden transition-all duration-300 basis-full grow sm:block">
      <div className="flex flex-col gap-5 mt-5 sm:flex-row sm:items-center sm:mt-0 sm:pl-5">
        <a className="font-medium text-blue-500" href="#" aria-current="page">Landing</a>
        <a className="font-medium text-gray-600 hover:text-gray-400 dark:text-gray-400 dark:hover:text-gray-500" href="#">Account</a>
        <a className="font-medium text-gray-600 hover:text-gray-400 dark:text-gray-400 dark:hover:text-gray-500" href="#">Work</a>
        <a className="font-medium text-gray-600 hover:text-gray-400 dark:text-gray-400 dark:hover:text-gray-500" href="#">Blog</a>
      </div>
    </div>
  </nav>
</header>
      </>
  );
}
export default Navbar;
