import React from 'react';

function ScrollToTopButton() {
  const handleClick = () => {
    window.scrollTo(0, 0);
  };

  return (
    <button onClick={handleClick}>
      Scroll to Top
    </button>
  );
}

export default ScrollToTopButton;
