import React from "react";
import { Link } from "react-router-dom";

function OtherPosts({ posts }) {
  // Burada sadece en fazla 3 yazıyı göstermek için bir filtreleme işlemi gerçekleştiriyoruz.
  const otherPosts = posts
    .filter((post) => post.url !== window.location.pathname) // Şu anki sayfanın kendisini filtrele.
    .slice(0, 3); // En fazla 3 yazı göster.

  return (
    <div className="other-posts">
      <h2>Diğer Yazılar</h2>
      <ul>
        {otherPosts.map((post) => (
          <li key={post.id}>
            <Link to={post.url}>
              <img src={`http://localhost:8000${post.img}`} alt={post.title} />
              {post.title}
            </Link>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default OtherPosts;
